    function handleTaskInput(event){
        if(event.key === 'Enter'){
            props.onAddItem(event.target.value)
        }

    }

    return(

            <header className="header">
            <h1>{props.title}</h1>
            <input className="new-todo"
                    placeholder="What needs to be done?"
                    onKeyDown={handleTaskInput}
                    autoFocus />
            </header>
    )
}
