import './App.css';
import Main from './components/Main';
import Footer from './components/Footer';
import Header from './components/Header';
import { useEffect, useState } from 'react';



function App() {
     const [todos, setTodos] = useState([]);
     const [nonCompletedItemsCount, setnonCompletedItemsCount] = useState(0);



     useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/todos')
        .then( response => response.json())
        .then(setTodos)

     }, [])

    useEffect(() => {
      const uncompleted = todos.filter( todo=> !todo.completed );
      setnonCompletedItemsCount(uncompleted.length)
    }, [todos])
         const appTitle = 'TodosApp';

    // Events
    const addTodo = (title) => {
      const newTodos =  todos.concat({id: Date.now(),title, completed: false})
      setTodos(newTodos)
    }

    const removeTodo = (todoToRemove) => {
        todos.filter(currentTodo => currentTodo.id !== todoToRemove.id);
    }

    // todo: where should we implemnt it?
    const markAsCompleted = () => {}

    const clearAllCompletedItems = (checkedValue) => {
        todos = todos.map( todo =>({ ...todo, completed: checkedValue}))

    }

    const toggleAllItems = () => {
        todos.filter(currentTodo => !currentTodo.completed);

        // todos = todos.map( todo => Object.assign({}, todo, {completed: checkedValue})), completed: checkedValue}))

    }

    // todo: pass title, placeholder to header
    // todo: pass item left to footer

    // todo: BONUS THINK ABOUT EDIT IN PLACE (DOUBLE CLICK TO VHANGE ITEM)

    return (
        <section className="todoapp">
          <Header title={appTitle}
                  onAddItem={addTodo}
                  text="What needs to be done?"  />
          <Main items={todos}
                onToggleAll={toggleAllItems} />
          <Footer 
                  itemLeftCount ={nonCompletedItemsCount}
                  onClearCompleted={clearAllCompletedItems}/>
        </section>
    )
